import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, jsonb, timestamp, serial } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table (keeping existing structure)
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Projects table for saving project history
export const projects = pgTable("projects", {
  id: serial("id").primaryKey(),
  sessionId: text("session_id").notNull(),
  name: text("name").notNull(),
  prompt: text("prompt").notNull(),
  templateId: text("template_id"),
  files: jsonb("files").notNull().$type<ProjectFile[]>(),
  dependencies: jsonb("dependencies").notNull().$type<Record<string, string>>(),
  createdAt: timestamp("created_at", { withTimezone: true }).notNull().defaultNow(),
  updatedAt: timestamp("updated_at", { withTimezone: true }).notNull().defaultNow(),
});

export const insertProjectSchema = createInsertSchema(projects).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertProject = z.infer<typeof insertProjectSchema>;
export type Project = typeof projects.$inferSelect;

// Project generation data structures (in-memory only, no DB)
export interface ProjectFile {
  path: string;
  content: string;
  language: string;
}

export interface GenerationRequest {
  prompt: string;
  template?: 'react-vite' | 'nextjs' | 'vanilla';
}

export interface GenerationResponse {
  files: ProjectFile[];
  dependencies: Record<string, string>;
  projectName: string;
  description: string;
}

export interface GenerationProgress {
  step: string;
  currentFile?: string;
  progress: number;
  complete: boolean;
}

// Validation schemas
export const generationRequestSchema = z.object({
  prompt: z.string().min(10, "Prompt must be at least 10 characters").max(2000, "Prompt too long"),
  template: z.enum(['react-vite', 'nextjs', 'vanilla']).optional(),
});

export type ValidatedGenerationRequest = z.infer<typeof generationRequestSchema>;

// Template configurations
export interface Template {
  id: 'react-vite' | 'nextjs' | 'vanilla';
  name: string;
  description: string;
  icon: string;
  techStack: string[];
  files: ProjectFile[];
  dependencies: Record<string, string>;
}

// Available templates with full details
export const TEMPLATES: Template[] = [
  {
    id: 'react-vite',
    name: 'React + Vite',
    description: 'Modern React application with Vite as the build tool. Lightning-fast HMR and optimized for development.',
    icon: 'react',
    techStack: ['React', 'TypeScript', 'Vite', 'ES Modules'],
    files: [],
    dependencies: {
      'react': '^18.2.0',
      'react-dom': '^18.2.0'
    }
  },
  {
    id: 'nextjs',
    name: 'Next.js App Router',
    description: 'Full-stack React framework with server-side rendering, App Router, and built-in optimizations.',
    icon: 'nextjs',
    techStack: ['Next.js', 'React', 'TypeScript', 'App Router'],
    files: [],
    dependencies: {
      'next': '^14.0.0',
      'react': '^18.2.0',
      'react-dom': '^18.2.0'
    }
  },
  {
    id: 'vanilla',
    name: 'Vanilla JavaScript',
    description: 'Pure HTML, CSS, and JavaScript with no build tools or frameworks. Simple and lightweight.',
    icon: 'vanilla',
    techStack: ['HTML5', 'CSS3', 'JavaScript', 'No Build Tools'],
    files: [],
    dependencies: {}
  }
];

// SSE Event Types for streaming
// Note: The 'type' field is sent as the SSE event name, not in the data payload
export interface StatusEventData {
  message: string;
}

export interface FileEventData {
  fileName: string;
  progress: number;
}

export interface CompleteEventData {
  project: GenerationResponse;
}

export interface ErrorEventData {
  error: string;
}

// Full event structure (used internally in generators)
export interface StatusEvent {
  type: 'status';
  data: StatusEventData;
}

export interface FileEvent {
  type: 'file';
  data: FileEventData;
}

export interface CompleteEvent {
  type: 'complete';
  data: CompleteEventData;
}

export interface ErrorEvent {
  type: 'error';
  data: ErrorEventData;
}

export type StreamEvent = StatusEvent | FileEvent | CompleteEvent | ErrorEvent;

